size(150);

defaultpen(linewidth(0.7) + fontsize(10));
pair A,B,C,D;
A = (0,0);
B=(4,0);
C =(7,0);
D = (7,3*sqrt(3));
draw (A--B--D--A);
draw(B--C--D,dashed);
