A = (0,0);
B = 4*dir(40);
C = B + 5*dir(60);
D = C + 6*dir(30);
E = (D.x,0);
P = (B.x,0);
Q = (C.x,B.y);
R = (D.x,C.y);
trans = (14,0);

draw(A--B--P--cycle);
draw(B--C--Q--cycle);
draw(C--D--R--cycle);
draw(P--E--R,dashed);

label("$x$", (A + P)/2, S, red);
label("$\sqrt{16 - x^2}$", (B + P)/2, dir(0), red);
label("$4$", (A + B)/2, NW, red);
label("$y$", (B + Q)/2, S, red);
label("$\sqrt{25 - y^2}$", (C + Q)/2, dir(0), red);
label("$5$", (B + C)/2, NW, red);
label("$z$", (C + R)/2, S, red);
label("$\sqrt{36 - z^2}$", (D + R)/2, dir(0), red);
label("$6$", (C + D)/2, NW, red);
label("$A$", A, SW);
label("$B$", B, NW);
label("$C$", C, NW);
label("$D$", D, NE);
