Ap = (9,0);
B = (0,0);
C = (12,0);
P = x*dir(60);
Q = C + y*dir(120);

draw(B--C--Q--P--cycle);
draw(P--Ap--Q);
draw(P--A--Q,dashed);

label("$A$", A, N);
label("$A'$", Ap, S);
label("$B$", B, SW);
label("$C$", C, SE);
label("$P$", P, NW);
label("$Q$", Q, NE);
[/asy]

