dot("$54^\circ$", dir(54), dir(54));
dot("$90^\circ$", dir(90), NE, red);
dot("$126^\circ$", dir(126), dir(126));
dot("$162^\circ$", dir(162), dir(162));
dot("$198^\circ$", dir(198), dir(198));
dot("$234^\circ$", dir(234), dir(234));
